import { makeStyles } from '@material-ui/core/styles';

export default makeStyles({
  card: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '100%',
    height: '65vh',
    padding: '10%',
    borderRadius: 10,
    color: 'white',
  },
  infoCard: {
    display: '2vh', flexDirection: 'column', textAlign: 'center', fontSize: '2vh',
  },
  container: {
    padding: '0 5%', width: '100%', margin: 0,
  },
});
